{"bullets":".custom-01 .tp-bullet {
	background-color: #000;
    border-radius: 50%;
    -webkit-border-radius: 50%;
    -moz-border-radius: 50%;
    border: 2px solid #fff;
    position: relative;
    display: inline-block;
    transition: all 0.3s;
    -webkit-transition: all 0.3s;
    -moz-transition: all 0.3s;
    width: 11px;
    height: 11px;
}
.custom-01 .tp-bullet:before {
    z-index: 1;
	position: absolute;
    content: \'\';
    display: block;
    left: 1px;
    right: 1px;
    top: 1px;
    bottom: 1px;
    background-color: #fff;
    border-radius: 50%;
    -webkit-border-radius: 50%;
    -moz-border-radius: 50%;
    transition: all 0.3s;
    -webkit-transition: all 0.3s;
    -moz-transition: all 0.3s;
    opacity: 0;
}
.custom-01 .tp-bullet:after {
    z-index: 2;
	position: absolute;
    content: \'\';
    display: block;
    left: 50%;
    right: 50%;
    top: 50%;
    bottom: 50%;
    background-color: #6ea820;
    border-radius: 50%;
    -webkit-border-radius: 50%;
    -moz-border-radius: 50%;
    transition: all 0.3s;
    -webkit-transition: all 0.3s;
    -moz-transition: all 0.3s;
    opacity: 0;
}
.custom-01 .tp-bullet:hover,
.custom-01 .tp-bullet:focus,
.custom-01 .tp-bullet.selected {
	background-color: #6ea820;
    border-color: #6ea820;
}
.custom-01 .tp-bullet:hover:before,
.custom-01 .tp-bullet:focus:before,
.custom-01 .tp-bullet.selected:before {
	left: -10px;
    right: -10px;
    bottom: -10px;
    top: -10px;
    opacity: 1;
}
.custom-01 .tp-bullet:hover:after,
.custom-01 .tp-bullet:focus:after,
.custom-01 .tp-bullet.selected:after {
	left: 0px;
    right: 0px;
    bottom: 0px;
    top: 0px;
    opacity: 1;
}
","arrows":".custom-01.tparrows {
	cursor:pointer;
	background-color: #fff;
	position:absolute;
	display:block;
	z-index:100;
    height: 90px;
    line-height: 90px;
    width: 90px;
    -webkit-transition: all 0.3s;
    -moz-transition: all 0.3s;
    transition: all 0.3s;
}
.custom-01.tparrows:before {
	font-family: \"Flaticon\";
	font-size: 34px;
	color: #b7b7b7;
	display:block;
	line-height: 90px;
	text-align: center;
    -webkit-transition: color 0.3s;
    -moz-transition: color 0.3s;
    transition: color 0.3s;
    z-index:2;
    position:relative;
}
.custom-01.tparrows.tp-leftarrow:before {
    content: \"\\f141\";
}
.custom-01.tparrows.tp-rightarrow:before {
    content: \"\\f142\";
}
.custom-01.tparrows:hover {
	background-color: #2e3192;
}
.custom-01.tparrows:hover:before {
 	color: #fff;
}"}
