{"arrows":".custom-01.tparrows {
	cursor:pointer;
	background-color: #fff;
	position:absolute;
	display:block;
	z-index:100;
    height: 90px;
    line-height: 90px;
    width: 90px;
    -webkit-transition: all 0.3s;
    -moz-transition: all 0.3s;
    transition: all 0.3s;
}
.custom-01.tparrows:before {
	font-family: \"Flaticon\";
	font-size: 34px;
	color: #b7b7b7;
	display:block;
	line-height: 90px;
	text-align: center;
    -webkit-transition: color 0.3s;
    -moz-transition: color 0.3s;
    transition: color 0.3s;
    z-index:2;
    position:relative;
}
.custom-01.tparrows.tp-leftarrow:before {
    content: \"\\f141\";
}
.custom-01.tparrows.tp-rightarrow:before {
    content: \"\\f142\";
}
.custom-01.tparrows:hover {
	background-color: #000;
}
.custom-01.tparrows:hover:before {
 	color: #fff;
}

.custom-01.tp-rightarrow {
	background-color: #000;
}
.custom-01.tp-rightarrow:before {
    color: #fff;
}"}
