{"bullets":".custom-01 .tp-bullet {
	background-color: #000;
    border-radius: 50%;
    -webkit-border-radius: 50%;
    -moz-border-radius: 50%;
    border: 2px solid #fff;
    position: relative;
    display: inline-block;
    transition: all 0.3s;
    -webkit-transition: all 0.3s;
    -moz-transition: all 0.3s;
    width: 11px;
    height: 11px;
}
.custom-01 .tp-bullet:before {
    z-index: 1;
	position: absolute;
    content: \'\';
    display: block;
    left: 1px;
    right: 1px;
    top: 1px;
    bottom: 1px;
    background-color: #fff;
    border-radius: 50%;
    -webkit-border-radius: 50%;
    -moz-border-radius: 50%;
    transition: all 0.3s;
    -webkit-transition: all 0.3s;
    -moz-transition: all 0.3s;
    opacity: 0;
}
.custom-01 .tp-bullet:after {
    z-index: 2;
	position: absolute;
    content: \'\';
    display: block;
    left: 50%;
    right: 50%;
    top: 50%;
    bottom: 50%;
    background-color: #6ea820;
    border-radius: 50%;
    -webkit-border-radius: 50%;
    -moz-border-radius: 50%;
    transition: all 0.3s;
    -webkit-transition: all 0.3s;
    -moz-transition: all 0.3s;
    opacity: 0;
}
.custom-01 .tp-bullet:hover,
.custom-01 .tp-bullet:focus,
.custom-01 .tp-bullet.selected {
	background-color: #6ea820;
    border-color: #6ea820;
}
.custom-01 .tp-bullet:hover:before,
.custom-01 .tp-bullet:focus:before,
.custom-01 .tp-bullet.selected:before {
	left: -10px;
    right: -10px;
    bottom: -10px;
    top: -10px;
    opacity: 1;
}
.custom-01 .tp-bullet:hover:after,
.custom-01 .tp-bullet:focus:after,
.custom-01 .tp-bullet.selected:after {
	left: 0px;
    right: 0px;
    bottom: 0px;
    top: 0px;
    opacity: 1;
}
","arrows":".custom-01.tparrows {
	cursor:pointer;
	background-color: #fff;
	position:absolute;
	display:block;
	z-index:100;
    height: 120px;
    line-height: 120px;
    width: 125px;
    -webkit-transition: all 0.3s;
    -moz-transition: all 0.3s;
    transition: all 0.3s;
}
.custom-01.tparrows:after {
	content: \'\';
    display: block;
    position: absolute;
    top: 50%;
    width: 1px;
    height: 80px;
    transform: translateY(-50%);
    -webkit-transform: translateY(-50%);
    -moz-transform: translateY(-50%);
    background-color: #e0e0e0;
}
.custom-01.tp-leftarrow:after {
	right: 0;
}
.custom-01.tp-rightarrow:after {
	left: 0;
}
.custom-01.tparrows:hover + .tparrows:after {
	display: none;
}
.custom-01.tparrows:before {
	font-family: \"Flaticon\";
	font-size: 34px;
	color: #696969;
	display:block;
	line-height: 120px;
	text-align: center;
    -webkit-transition: color 0.3s;
    -moz-transition: color 0.3s;
    transition: color 0.3s;
    z-index:2;
    position:relative;
}
.custom-01.tparrows.tp-leftarrow:before {
    content: \"\\f141\";
}
.custom-01.tparrows.tp-rightarrow:before {
    content: \"\\f142\";
}
.custom-01.tparrows:hover:before {
 	color: #000;
}"}

{"bullets":"body {
counter-reset: section;
}
.custom-04 .tp-bullet {
background: transparent;
font-size: 0;
}
.custom-04 .tp-bullet:before {
position: absolute;
left: 50%;
top: 50%;
-moz-transform: translate(-50%, -50%);
-webkit-transform: translate(-50%, -50%);
-o-transform: translate(-50%, -50%);
transform: translate(-50%, -50%);
counter-increment: section;
content: \'0\'counter(section);
display: block;
font-size: 16px;
font-family: \'Montserrat\';
color: #fff;
-webkit-transition: all 0.2s;
-moz-transition: all 0.2s;
-o-transition: all 0.2s;
transition: all 0.2s;
}
.custom-04 .tp-bullet:after {
content: \'\';
display: block;
position: absolute;
top: 50%;
left: 50%;
width: 50px;
height: 1px;
-moz-transform: translate(-50%, -50%) rotate(-20deg) scale(0);
-webkit-transform: translate(-50%, -50%) rotate(-20deg) scale(0);
-o-transform: translate(-50%, -50%) rotate(-20deg) scale(0);
transform: translate(-50%, -50%) rotate(-20deg) scale(0);
-webkit-transition: all 0.2s;
-moz-transition: all 0.2s;
-o-transition: all 0.2s;
transition: all 0.2s;
background-color: #fff;
}
.custom-04 .tp-bullet:hover:before,
.custom-04 .tp-bullet.selected:before{
color: #6ea820;
font-family: \'Playfair Display\';
font-style: italic;
-moz-transform: translate(-50%, -50%) scale(1.8);
-webkit-transform: translate(-50%, -50%) scale(1.8);
-o-transform: translate(-50%, -50%) scale(1.8);
transform: translate(-50%, -50%) scale(1.8);
margin-top: -5px;
}
.custom-04 .tp-bullet:hover:after,
.custom-04 .tp-bullet.selected:after {
-moz-transform: translate(-50%, -50%) rotate(-20deg) scale(1);
-webkit-transform: translate(-50%, -50%) rotate(-20deg) scale(1);
-o-transform: translate(-50%, -50%) rotate(-20deg) scale(1);
transform: translate(-50%, -50%) rotate(-20deg) scale(1);
}"}

{"bullets":".custom-02 .tp-bullet {
