    -moz-border-radius: 50%;
    -o-border-radius: 50%;
    background-color: #000;
    position: relative;
    display: inline-block;
}
.custom-02 .tp-bullet:before {
	content: \'\';
    display: block;
    position: absolute;
    left: 8px;
    right: 8px;
    top: 8px;
    bottom: 8px;
    border: 1px solid #6ea820;
    opacity: 0;
    transition: all 0.3s;
    -webkit-transition: all 0.3s;
    -moz-transition: all 0.3s;
    border-radius: 50%;
    -webkit-border-radius: 50%;
    -moz-border-radius: 50%;
    -o-border-radius: 50%;
}
.custom-02 .tp-bullet:hover:before,
.custom-02 .tp-bullet.selected:before{
	left: 0;
    top: 0;
    right: 0;
    bottom: 0;
    opacity: 1;
}","arrows":".custom-02.tparrows {
width: 70px;
height: 70px;
background-color: #000;
}
.custom-02.tparrows:before {
height: 70px;
line-height: 70px;
font-family: \'Flaticon\';
color: #fff;
transition: all 0.3s;
-moz-transition: all 0.3s;
-webkit-transition: all 0.3s;
}
.custom-02.tp-leftarrow:before {
content: \'\\f140\';
}
.custom-02.tp-rightarrow:before {
content: \'\\f135\';
}
